import {
  SimpleGrid,
  Stack,
  Input,
  Textarea,
  InputLeftElement,
  InputGroup,
  Box,
  RadioGroup,
  Radio,
  useRadio,
  useRadioGroup,
} from "@chakra-ui/react";
import { ChatIcon, EmailIcon, PhoneIcon } from "@chakra-ui/icons";
import React, { useState } from "react";
import _startCase from "lodash/startCase";

const ContactForm = () => {
  const [contact, setContact] = useState({
    name: "",
    last: "",
    email: "",
    message: "",
    phone: "",
    reason: "",
  });

  const handleChange = ({ currentTarget, target, ...e }) => {
    console.log({ currentTarget, target, e });
    setContact({
      ...contact,
      [currentTarget.name]: currentTarget.value,
    });
  };

  const RadioCard = (props) => {
    const { getInputProps, getCheckboxProps } = useRadio(props);
    const input = getInputProps();
    const checkbox = getCheckboxProps();
    return (
      <Box as="label">
        <input {...input} />
        <Box
          {...checkbox}
          cursor="pointer"
          borderWidth="1px"
          borderRadius="md"
          boxShadow="md"
          minWidth="150px"
          textAlign="center"
          transition="all .2s ease-in-out"
          _checked={{
            bg: "gray.800",
            color: "gray.100",
            borderColor: "black.600",
            transform: "scale(1.05)",
          }}
          _focus={{
            boxShadow: "outline",
          }}
          px={5}
          py={3}
        ></Box>
      </Box>
    );
  };

  const options = ["business_inquiry", "new_player", "other"];

  const { getRootProps, getRadioProps } = useRadioGroup({
    name: "reason",
    defaultValue: "business_inquiry",
    onChange: (reason) => setContact({ ...contact, reason }),
  });

  const group = getRootProps();

  return (
    <Box
      maxW="5xl"
      borderWidth="2px"
      overflow="hidden"
      borderRadius="2xl"
      borderColor="gray.light"
      cursor="pointer"
      p="8"
      bg="gray.700"
    >
      <SimpleGrid direction="column" columns={1} spacing={8}>
        Any questions or comments? Leave us a message!
        <Stack direction="row" spacing={3}>
          <Input
            sx={{
              color: "gray",
              _focus: {
                color: "black",
              },
            }}
            variant="filled"
            placeholder="Name"
            name="name"
            value={contact.name}
          />
          <Input
            sx={{
              color: "gray",
              _focus: {
                color: "black",
              },
            }}
            variant="filled"
            placeholder="Last"
            name="last"
            value={contact.last}
          />
        </Stack>
        <Stack spacing={8}>
          <InputGroup>
            <InputLeftElement
              pointerEvents="none"
              children={<EmailIcon color="gray.400" />}
            />
            <Input
              sx={{
                color: "gray",
                _focus: {
                  color: "black",
                },
              }}
              type="email"
              variant="filled"
              placeholder="Email"
              name="email"
              value={contact.email}
            />
          </InputGroup>
          <InputGroup>
            <InputLeftElement
              pointerEvents="none"
              children={<PhoneIcon color="gray.400" />}
            />
            <Input
              sx={{
                color: "gray",
                _focus: {
                  color: "black",
                },
              }}
              variant="filled"
              type="tel"
              placeholder="Phone number"
              name="phone"
              value={contact.phone}
            />
          </InputGroup>

          <Stack
            {...group}
            direction="row"
            spacing={2}
            justifyContent="space-evenly"
          >
            {options.map((value) => {
              const radio = getRadioProps({ value });
              return (
                <RadioCard key={value} {...radio}>
                  {_startCase(value)}
                </RadioCard>
              );
            })}
          </Stack>

          <Textarea
            sx={{
              color: "gray",
              _focus: {
                color: "black",
              },
            }}
            variant="filled"
            type="text"
            placeholder="Message"
          />
        </Stack>
      </SimpleGrid>
    </Box>
  );
};

export default ContactForm;
