import {
  Box,
  Button,
  Flex,
  Link,
  LinkBox,
  Stack,
  Text,
  useBreakpointValue,
} from "@chakra-ui/react";
import React from "react";
import styled from "styled-components";

const Diagonal = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  padding: 5%;
  min-height: 70vh;
  background-repeat: no-repeat;
  background-position: top;
  background-size: cover;
  background-image: ${(props) => `linear-gradient(
      120deg,
      #000 40%,
      #00000060 40%,
      #00000060 50%,
      transparent 50%,
      transparent
    ),
    url(${props.image});`};
`;
const Hero = ({ text, buttons = [], image, size }) => {
  const variant = useBreakpointValue({ base: "solid", md: "outline" });

  return (
    <Diagonal image={image} {...size}>
      <Flex
        direction="row"
        align="center"
        justify="space-evenly"
        width={{
          sm: "100%",
          md: "50%",
        }}
      >
        <Box>
          <Text
            fontSize="5xl"
            casing="uppercase"
            as="b"
            color="white"
            style={{ marginBottom: 12 }}
          >
            {text}
          </Text>
          <Stack spacing={4} direction="row" align="center" marginTop={8}>
            {buttons &&
              buttons?.map(({ link, display, color }) => {
                return (
                  <LinkBox>
                    <Link>
                      <Button
                        as={link ? "a" : "button"}
                        colorScheme="gray"
                        size="md"
                        color={color || "brand.400"}
                        href={link || ""}
                        textDecoration="none"
                      >
                        {display}
                      </Button>
                    </Link>
                  </LinkBox>
                );
              })}
          </Stack>
        </Box>
      </Flex>
      <Box flex="1" color="transparent">
        o
      </Box>
    </Diagonal>
  );
};

export default Hero;
