import { getStrapiMedia } from "../lib/media";

const Image = ({
  image = {
    alternativeText: "",
    name: "",
    url: "",
  },
  style,
}) => {
  const imageUrl = getStrapiMedia(image);

  return (
    <img
      height="auto"
      width="100%"
      src={imageUrl}
      alt={image.alternativeText || image.name}
      style={style}
    />
  );
};

export default Image;
