import { Avatar } from "@chakra-ui/avatar";
import { Text, Flex, Box } from "@chakra-ui/layout";
import React from "react";
import Image from "../../common/Image";

const Team = ({ team: { name, score, logo }, away, preview }) => {
  const uploadsUrl = (url) => `http://localhost:1339${url}`;

  const direction = away ? "row-reverse" : "row";
  return (
    <>
      <Flex
        justifyContent="space-between"
        alignItems="center"
        direction={direction}
      >
        <Flex
          justifyContent="space-between"
          alignItems="center"
          direction={preview ? "row-reverse" : direction}
        >
          <Box>
            <Text
              fontWeight={name === "Bombers" ? "extrabold" : "normal"}
              my={0}
              mx={2}
              fontSize="lg"
              fontFamily="Big Shoulders Display"
              textTransform={preview ? "uppercase" : "capitalize"}
              color={preview ? "white" : "inherit"}
            >
              {name}
            </Text>
          </Box>
          <Box m={4}>
            <Avatar
              bg="brand.200"
              showBorder
              size="sm"
              name={name}
              src={uploadsUrl(logo?.url || "")}
            />
          </Box>
        </Flex>
        {score && (
          <Box>
            <Text
              fontWeight={"extrabold"}
              my={0}
              mx={2}
              fontSize="2xl"
              fontFamily="body"
              textTransform="capitalize"
              bgColor={preview ? "#e2e2e2" : "none"}
              p={2}
            >
              {score}
            </Text>
          </Box>
        )}
      </Flex>
    </>
  );
};

export default Team;
