import {
  SimpleGrid,
  Stack,
  Input,
  Textarea,
  InputLeftElement,
  InputGroup,
  Box,
} from "@chakra-ui/react";
import { ChatIcon, EmailIcon, PhoneIcon } from "@chakra-ui/icons";
import React from "react";

const ContactForm = () => {
  return (
    <Box
      maxW="5xl"
      borderWidth="2px"
      overflow="hidden"
      borderRadius="2xl"
      borderColor="gray.light"
      cursor="pointer"
      sx={{
        transition: "all .2s ease-in-out",
        boxShadow: "0px 5px 10px #212121",
        _hover: {
          transform: "scale(1.05)",
          boxShadow: "0px 5px 10px #21212150",
        },
      }}
    >
      <SimpleGrid
        direction="column"
        columns={1}
        spacing={8}
        bg="gray.500"
        p="8"
        borderRadius="15"
      >
        <Stack direction="row" spacing={3}>
          <Input variant="filled" placeholder="Name" />
          <Input variant="filled" placeholder="Last" />
        </Stack>
        <Stack spacing={8}>
          <InputGroup>
            <InputLeftElement
              pointerEvents="none"
              children={<EmailIcon color="gray.400" />}
            />
            <Input type="email" variant="filled" placeholder="Email" />
          </InputGroup>
          <InputGroup>
            <InputLeftElement
              pointerEvents="none"
              children={<PhoneIcon color="gray.400" />}
            />
            <Input variant="filled" type="tel" placeholder="Phone number" />
          </InputGroup>

          <InputGroup>
            <InputLeftElement
              pointerEvents="none"
              children={<ChatIcon color="gray.400" />}
            />
            <Textarea variant="filled" type="text" placeholder="Message" />
          </InputGroup>
        </Stack>
      </SimpleGrid>
    </Box>
  );
};

export default ContactForm;
