import { SimpleGrid } from "@chakra-ui/layout";
// import Link from "next/link";
import React, { useState } from "react";
import GameCard from "./GameCard";

const Games = ({ games }) => {
  console.log({ games });
  const [upcoming, setUpcoming] = useState(
    games.filter((game) => !game.result)
  );
  const [played, setPlayed] = useState(games.filter((game) => game.result));

  console.log({ played, upcoming });

  return (
    <>
      <SimpleGrid spacing="8">
        <GameCard game={{}} title="Next Game" link="/schedule" />
        <GameCard game={{}} title="Last Game" link="/result" />
      </SimpleGrid>
    </>
  );
};

export default Games;
