import { SimpleGrid, Flex, Box, Grid, GridItem } from "@chakra-ui/layout";
// import Link from "next/link";
import React, { useState } from "react";
import GameCard from "./GameCard";

const Games = ({ games }) => {
  const upcoming = games.filter((game) => !game.finished);
  const played = games.filter((game) => game.finished);

  const gameCards = [
    {
      game: played[0],
      title: "Last Game",
      link: "/schedule",
      linkDisplay: "Results",
    },
    {
      game: upcoming[0],
      title: "Next Game",
      link: "/schedule",
      linkDisplay: "Schedule",
    },
  ];

  return (
    <Grid
      py="8"
      h="auto"
      templateRows="repeat(2, 1fr)"
      templateColumns="repeat(2, 1fr)"
      gap={4}
    >
      {/* 
        <GameCard
          game={played[0]}
          title="Last Game"
          link="/result"
          linkDisplay="Results"
        /> */}
      <GridItem colSpan={[1, 2, 2, 1]} rowSpan={[2, 1, 1, 2]}>
        <GameCard
          game={upcoming[0]}
          title="Next Game"
          link="/schedule"
          linkDisplay="schedule"
        />
      </GridItem>
    </Grid>
  );
};

export default Games;
