import { Flex, Text, Heading } from "@chakra-ui/react";
import Nav from "./nav";

const Layout = ({ children, categories, seo, header }) => (
  <Flex height="100vh" direction="column">
    <Nav categories={categories} />
    {header && (
      <Heading as="h1" size="4xl">
        {header}
      </Heading>
    )}
    {children}
  </Flex>
);

export default Layout;
