import { SimpleGrid, Flex, Box, Grid, GridItem } from "@chakra-ui/layout";
// import Link from "next/link";
import React, { useState } from "react";
import GameCard from "./GameCard";

const Games = ({ games }) => {
  console.log({ games });
  const upcoming = games.filter((game) => !game.finished);
  const played = games.filter((game) => game.finished);

  const gameCards = [
    {
      game: played[0],
      title: "Last Game",
      link: "/schedule",
      linkDisplay: "Results",
    },
    {
      game: upcoming[0],
      title: "Next Game",
      link: "/schedule",
      linkDisplay: "Schedule",
    },
  ];

  return (
    <Grid
      h="auto"
      templateRows="repeat(2, 1fr)"
      templateColumns="repeat(2, 1fr)"
      gap={4}
    >
      {gameCards.map((game) => (
        <GridItem colSpan={[1, 2, 2, 1]} rowSpan={[2, 1, 1, 2]}>
          <GameCard {...game} />
        </GridItem>
      ))}
    </Grid>
  );
};

export default Games;
