import { SimpleGrid } from "@chakra-ui/layout";
// import Link from "next/link";
import React, { useState } from "react";
import GameCard from "./GameCard";

const Games = ({ games }) => {
  const upcoming = games.filter((game) => !game.finished) || [
    {
      away: {
        id: 1,
        name: "Bombers",
        division: "D1",
        bio:
          "The St. Louis Bombers Rugby Football Club arrived …man, who the Black side MVP award is named after.",
        location: "St. Louis, MO",
      },
      away_score: null,
      created_at: "2021-10-04T05:07:40.764Z",
      date: "2021-10-09T18:00:00.000Z",
      division: "d1",
      finished: false,
      home: {
        id: 2,
        name: "Palmer",
        division: "D1",
        bio: "Palmer bio",
        location: "Davenport, IA",
      },
      home_score: null,
      id: 3,
      location: "Palmer Field",
      preview: null,
      slug: "d1-bombers-at-palmer",
      updated_at: "2021-10-04T05:34:51.283Z",
      updates: null,
      winner: null,
    },
  ];
  const played = games.filter((game) => game.finished);

  return (
    <>
      <SimpleGrid spacing="4" columns={1}>
        <GameCard
          game={upcoming[0]}
          title="Next Game"
          link="/schedule"
          linkDisplay="schedule"
        />
        <GameCard
          game={played[0]}
          title="Last Game"
          link="/result"
          linkDisplay="Results"
        />
      </SimpleGrid>
    </>
  );
};

export default Games;
