import {
  Accordion,
  AccordionButton,
  AccordionIcon,
  AccordionItem,
  AccordionPanel,
} from "@chakra-ui/accordion";
import { Avatar } from "@chakra-ui/avatar";
import { Box, Divider, Flex, Stack, Text } from "@chakra-ui/layout";
import React from "react";

const Schedule = ({ upcoming }) => {
  const formatDateTime = (dateTime) => {
    const date = new Date(dateTime);
    console.log({ date });
    const formattedDate =
      date.toLocaleDateString("en-US", { weekday: "short" }) +
      " " +
      date.toLocaleDateString("en-US", { month: "short" }) +
      " " +
      date.toLocaleDateString("en-US", { day: "2-digit" });
    const time = date.toLocaleString("en-US", {
      hour: "2-digit",
      minute: "2-digit",
    });
    return {
      date: formattedDate,
      time,
    };
  };

  return (
    <Accordion allowMultiple defaultIndex={[0]}>
      {upcoming.map((game) => {
        return (
          <AccordionItem key={game.slug}>
            <AccordionButton>
              <Stack
                direction="row"
                textAlign="left"
                alignItems="center"
                justifyContent="start"
                w="100%"
              >
                <Box>
                  <Text
                    m={0}
                    fontWeight="light"
                    fontSize="md"
                    textTransform="uppercase"
                    fontFamily="body"
                  >
                    {formatDateTime(game.date).date}
                  </Text>
                </Box>
                <Divider size="xl" orientation="vertical"></Divider>
                <Box flex="1">
                  <Text
                    m={0}
                    fontWeight="bold"
                    fontSize="lg"
                    textTransform="uppercase"
                    fontFamily="body"
                  >
                    {game?.home?.team_name} - {game?.away?.team_name}
                  </Text>
                </Box>
                <AccordionIcon />
              </Stack>
            </AccordionButton>
            <AccordionPanel pb={4}>
              <Stack
                direction="row"
                justifyContent="center"
                alignItems="center"
              >
                <Flex justifyContent="space-between" alignItems="center">
                  <Box>
                    <Text
                      my={0}
                      mx={2}
                      fontSize="md"
                      fontFamily="body"
                      textTransform="capitalize"
                    >
                      {game.home?.team_name}
                    </Text>
                  </Box>
                  <Box>
                    <Avatar
                      showBorder
                      size="sm"
                      name={game.home.team_name}
                      src={`http://localhost:1339${game.home.logo.url}`}
                    />
                  </Box>
                </Flex>
                <Box
                  bg="brand.500"
                  borderRadius="md"
                  p="2"
                  fontSize="xs"
                  color="white"
                >
                  {formatDateTime(game.date).time}
                </Box>
              </Stack>
            </AccordionPanel>
          </AccordionItem>
        );
      })}
    </Accordion>
  );
};

export default Schedule;
