import ReactMarkdown from "react-markdown";
import Moment from "react-moment";
import { fetchAPI } from "../../lib/api";
import Layout from "../../components/layout";
import Image from "../../common/image";
import Seo from "../../components/seo";
import { getStrapiMedia } from "../../lib/media";
import gfm from "remark-gfm";
import styled from "styled-components";
import { Box, Divider, Flex, Text } from "@chakra-ui/layout";
import { useBreakpointValue } from "@chakra-ui/media-query";

const ArticleHeader = styled.div`
  font-size: 44px;
  color: white;
  font-weight: 600;
  background: linear-gradient(
    90deg,
    rgba(24, 24, 24, 1) 0%,
    rgba(33, 33, 33, 1) 35%,
    rgba(48, 48, 48, 1) 100%
  );
  padding: 16px;
  border: 4px solid white;
  width: 100%;
  text-align: center;
`;

const Article = ({ article, categories }) => {
  const imageUrl = getStrapiMedia(article.image);

  const seo = {
    metaTitle: article.title,
    metaDescription: article.description,
    shareImage: article.image,
    article: true,
  };

  const textVariants = useBreakpointValue({
    xs: "lg",
    s: "lg",
    md: "5xl",
    lg: "6xl",
    xl: "6xl",
  });

  return (
    <Layout categories={categories}>
      <Seo seo={seo} />

      <Flex
        alignItems="flex-end"
        id="banner"
        h="md"
        className="uk-background-cover"
        data-src={imageUrl}
        data-srcset={imageUrl}
        data-uk-img
      >
        <Box mx="auto" my="0" w="50%">
          <Text
            color="brand.400"
            as="h1"
            fontSize={textVariants}
            fontWeight="bolder"
            textTransform="uppercase"
            textAlign="start"
            mb="8"
          >
            {article.title}
          </Text>
        </Box>
      </Flex>
      <div className="uk-section" style={{ border: "4px solid black" }}>
        <div className="uk-container uk-container-small">
          <div className="uk-grid-small uk-flex-left" data-uk-grid="true">
            <div>
              {article.author.picture && (
                <Image
                  image={article.author.picture}
                  style={{
                    position: "static",
                    borderRadius: "50%",
                    height: 30,
                  }}
                />
              )}
            </div>
            <div className="uk-width-expand">
              <p className="uk-margin-remove-bottom">
                By {article.author.name}
              </p>
              <p className="uk-text-meta uk-margin-remove-top">
                <Moment format="MMM Do YYYY">{article.published_at}</Moment>
              </p>
            </div>
          </div>
          <Divider size="2px" variant="solid" m="8"></Divider>

          <Box>
            <ReactMarkdown
              source={article.content}
              escapeHtml={false}
              remarkPlugins={[gfm]}
            />
          </Box>
          <hr className="uk-divider-small" />
        </div>
      </div>
    </Layout>
  );
};

export async function getStaticPaths() {
  const articles = await fetchAPI("/articles");

  return {
    paths: articles.map((article) => ({
      params: {
        slug: article.slug,
      },
    })),
    fallback: false,
  };
}

export async function getStaticProps({ params }) {
  const articles = await fetchAPI(
    `/articles?slug=${params.slug}&status=published`
  );
  const categories = await fetchAPI("/categories");

  return {
    props: { article: articles[0], categories },
    revalidate: 1,
  };
}

export default Article;
