import { Badge, Box, Text, SimpleGrid, Flex } from "@chakra-ui/layout";
import { Button, Link, LinkBox } from "@chakra-ui/react";

// import Link from "next/link";
import React, { useState, useEffect } from "react";
import ReactMarkdown from "react-markdown";
import Card from "../common/Card";
import Image from "../common/image";

const GameCard = ({ games }) => {
  const [upcoming, setUpcoming] = useState(
    games.filter((game) => !game.result)
  );
  const [played, setPlayed] = useState(games.filter((game) => game.result));

  console.log({ played, upcoming });

  return (
    <>
      <SimpleGrid spacing="8">
        <Flex direction="column">
          <Flex
            grow="1"
            justifyContent="space-between"
            direction="row"
            alignContent="center"
          >
            <Text color="gray.100" fontSize="3xl">
              Next Game
            </Text>
            <Link>
              <Button as="a" variant="rounded">
                Schedule
              </Button>
            </Link>
          </Flex>
          <Card>Next</Card>
        </Flex>
        <SimpleGrid>
          <Flex
            justifyContent="space-between"
            direction="row"
            alignContent="center"
          >
            <Text color="gray.100" fontSize="3xl">
              Last Game
            </Text>
            <Link>
              <Button as="a" variant="rounded">
                Results
              </Button>
            </Link>
          </Flex>
          <Card>Last</Card>
        </SimpleGrid>
      </SimpleGrid>
    </>
  );
};

export default GameCard;
