import React, { useEffect } from "react";
import ReactDOM from "react-dom";
import Layout from "../components/layout";

const contact = () => {
  const addPayPalScript = () => {
    const script = document.createElement("script");
    script.type = "text/javascript";
    script.src = `https://www.paypal.com/sdk/js?client-id=${process.env.PAYPAL_CLIENT_ID}`;
    // script.setAttribute("data-namespace", "paypal_sdk");

    script.async = true;
    script.onload = () => {
      setSdkReady(true);
    };
    document.body.appendChild(script);
  };
  useEffect(() => {
    console.log({ window });
    window.paypal
      .Buttons({
        style: {
          shape: "pill",
          color: "blue",
          layout: "vertical",
          label: "subscribe",
        },
        createSubscription: function (data, actions) {
          return actions.subscription.create({
            /* Creates the subscription */
            plan_id: "P-9NL41251R87142636MDWJ6MI",
          });
        },
        onApprove: function (data, actions) {
          alert(data.subscriptionID); // You can add optional success message for the subscriber here
        },
      })
      .driver("react", { React, ReactDOM })
      .render("#paypal-button-container-P-9NL41251R87142636MDWJ6MI");
  }, []);

  // function createSubscription(data, actions) {
  //   return actions.subscription.create({
  //     /* Creates the subscription */
  //     plan_id: "P-9NL41251R87142636MDWJ6MI",
  //   });
  // }

  // function onApprove(data, actions) {
  //   alert(data.subscriptionID); // You can add optional success message for the subscriber here
  // }

  return (
    <Layout>
      <div id="paypal-button-container-P-9NL41251R87142636MDWJ6MI"></div>
      {/* <PayPalButton
        createSubscription={createSubscription}
        onApprove={onApprove}
      ></PayPalButton> */}
    </Layout>
  );
};

export default contact;
