import { Badge, Box, LinkBox, Text } from "@chakra-ui/layout";
import Link from "next/link";
import React from "react";
import ReactMarkdown from "react-markdown";
import Image from "./image";

const GameCard = ({ game }) => {
  return (
    <Link as={`/game/${game.slug || "hello"}`} href="/game/[id]">
      <LinkBox>
        <Box
          maxW="5xl"
          borderWidth="2px"
          overflow="hidden"
          borderRadius="2xl"
          borderColor="gray.dark"
          cursor="pointer"
          sx={{
            transition: "all .2s ease-in-out",
            boxShadow: "0px 5px 10px #212121",
            _hover: {
              transform: "scale(1.05)",
              boxShadow: "0px 5px 10px #21212150",
            },
          }}
          bgGradient="gradient.main"
        >
          <Image image={game.image} sx={{ objectFit: "cover" }} />
          <Box p="4"></Box>
          <Box d="flex" alignItems="baseline" m={8}>
            <Badge borderRadius="full" px="2" bg="brand.800" color="brand.400">
              {game.category.name}
            </Badge>
            <Box
              color="gray.500"
              fontWeight="semibold"
              letterSpacing="wide"
              fontSize="xs"
              textTransform="uppercase"
            >
              {new Date(game.publishedAt).toLocaleDateString()}
            </Box>
          </Box>

          <Box bg="brand.transparent" p={8} m={8}>
            <Box
              mt="2"
              fontWeight="semibold"
              as="h1"
              lineHeight="tight"
              isTruncated
              pb={8}
            >
              <Text fontSize="2xl">{game.title}</Text>
            </Box>
            <ReactMarkdown>{game.description}</ReactMarkdown>
          </Box>
        </Box>
      </LinkBox>
    </Link>
  );
};

export default GameCard;
