import { Heading } from "@chakra-ui/layout";
import { groupBy, map } from "lodash";
import React from "react";
import Hero from "../../components/hero";
import Layout from "../../components/layout";
// import { groupBy, uniq } from "lodash";

const News = ({ articles }) => {
  console.log({ articles });

  const byCategory = articles.reduce((acc, article) => {
    const category = article.category.name;
    const newObj = { ...article, category: article.category.name };
    console.log({ newObj });
    return [...acc, newObj];
  }, []);

  const grouped = groupBy(byCategory, "category");

  console.log({ grouped });
  const g = map(grouped, (article) => console.log({ article }) || article);
  console.log({ g });
  return (
    <Layout>
      <Hero text="News and Articles" size="sm"></Hero>
      {map(grouped, (article, i, o) => {
        console.log({ article, i, o });
        <Heading as="h1" size="2xl">
          heading {i}
        </Heading>;
        return article.map((thing) => {
          return <div key={thing.slug}>{thing.title}</div>;
        });
      })}
    </Layout>
  );
};

export default News;

