import { Avatar } from "@chakra-ui/avatar";
import { Stack, Box, Wrap, WrapItem, Heading } from "@chakra-ui/layout";
import React from "react";
import Image from "../../common/Image";

const Sponsors = ({ sponsors }) => {
  return (
    <>
      <Heading as="h1" variant="" size="2xl" fontFamily="Big Shoulders Display">
        Sponsors
      </Heading>
      <Wrap
        spacing="8"
        align="center"
        justifyContent="space-evenly"
        m="16"
        p="4"
      >
        {sponsors.map((sponsor) => {
          const image = { ...sponsor.image, alternativeText: sponsor.name };

          return (
            sponsor.level === "gold" && (
              <WrapItem key={sponsor.name}>
                <Avatar
                  size="2xl"
                  name={sponsor.name}
                  src={`http://localhost:1339${sponsor.image.url}`}
                ></Avatar>
              </WrapItem>
            )
          );
        })}
      </Wrap>
    </>
  );
};

export default Sponsors;
