import { Flex, Box, Text, SimpleGrid } from "@chakra-ui/layout";
import {
  Button,
  Link,
  LinkBox,
  Tabs,
  TabList,
  TabPanel,
  TabPanels,
  Tab,
} from "@chakra-ui/react";
import React, { useState, useEffect } from "react";
import Articles from "../components/Articles/articles";
import Card from "../components/Articles/ArticleCard";
import Hero from "../common/Hero";
import Layout from "../common/Layout";
import Seo from "../common/Seo";
import { fetchAPI } from "../lib/api";
import Games from "../components/Games";
import ArticleCard from "../components/Articles/ArticleCard";
import { groupBy } from "lodash";

const Home = (props) => {
  const { articles, categories, homepage, games, ...rest } = props;
  const { d1, d3 } = groupBy(games, "division");

  const buttons = [
    {
      display: "Shop",
      link: "/shop",
    },
    {
      display: "Contact",
      link: "/contact",
    },
  ];

  return (
    <Layout categories={categories} seo={homepage.seo}>
      <Seo seo={homepage.seo} />
      <Hero
        text="St. Louis Bombers Rugby Club"
        image="images/mcb-hero.jpeg"
        buttons={buttons}
      />
      <SimpleGrid
        spacing={8}
        m="0"
        p="8"
        columns={{
          xs: 1,
          md: 1,
          lg: 2,
        }}
        gridTemplateColumns="repeat(1, 3fr 2fr)"
      >
        <Articles articles={articles} />
        <SimpleGrid gridTemplateRows="repeat(1, 2fr 3fr)">
          <Tabs isFitted>
            <TabList>
              <Tab>DI</Tab>
              <Tab>DIII</Tab>
            </TabList>
            <TabPanels>
              <TabPanel>
                <Games games={d1} />
              </TabPanel>
              <TabPanel>
                <Games games={d3} />
              </TabPanel>
            </TabPanels>
          </Tabs>
          <div>sponsors</div>
        </SimpleGrid>
      </SimpleGrid>
    </Layout>
  );
};

export async function getStaticProps() {
  // Run API calls in parallel
  const [
    articles,
    categories,
    homepage,
    games,
    players,
    coaches,
  ] = await Promise.all([
    fetchAPI("/articles?status=published"),
    fetchAPI("/categories"),
    fetchAPI("/homepage"),
    fetchAPI("/games"),
    fetchAPI("/players"),
    fetchAPI("/coaches"),
  ]);

  return {
    props: { articles, categories, homepage, games, players, coaches },
    revalidate: 1,
  };
}

export default Home;
