import React from "react";
import Team from "./Team";

const GameInfo = ({ homeTeam, awayTeam, time, location }) => {
  return (
    <Stack direction="row" justifyContent="center" alignItems="start" pt={8}>
      <Team team={homeTeam}></Team>
      <Stack>
        <Box bg="brand.500" borderRadius="md" p="2" fontSize="xs" color="white">
          {time}
        </Box>
        <Flex w="100%" justifyContent="center">
          <Box p="2" fontSize="xs">
            {location}
          </Box>
        </Flex>
      </Stack>
      <Team team={awayTeam} away></Team>
    </Stack>
  );
};

export default GameInfo;
