import { SimpleGrid } from "@chakra-ui/layout";
// import Link from "next/link";
import React, { useState } from "react";
import GameCard from "./GameCard";

const Games = ({ games }) => {
  console.log({ games });
  const [upcoming, setUpcoming] = useState(
    games.filter((game) => !game.result)
  );
  const [played, setPlayed] = useState(games.filter((game) => game.result));

  console.log("ðŸ”¥", { played, upcoming });

  return (
    <>
      <SimpleGrid spacing="4" columns={1}>
        <GameCard
          game={upcoming[0]}
          title="Next Game"
          link="/schedule"
          linkDisplay="schedule"
        />
        <GameCard
          game={played[0]}
          title="Last Game"
          link="/result"
          linkDisplay="Results"
        />
      </SimpleGrid>
    </>
  );
};

export default Games;
