import {
  SimpleGrid,
  Stack,
  Input,
  Textarea,
  InputLeftElement,
  InputGroup,
  Box,
  RadioGroup,
  Radio,
} from "@chakra-ui/react";
import { ChatIcon, EmailIcon, PhoneIcon } from "@chakra-ui/icons";
import React, { useState } from "react";

const ContactForm = () => {
  const [contact, setContact] = useState({
    name: "",
    last: "",
    email: "",
    message: "",
    phone: "",
    reason: "",
  });

  const handleChange = ({ currentTarget, target, ...e }) => {
    console.log({ currentTarget, target, e });
    setContact({
      ...contact,
      [currentTarget.name]: currentTarget.value,
    });
  };

  return (
    <Box
      maxW="5xl"
      borderWidth="2px"
      overflow="hidden"
      borderRadius="2xl"
      borderColor="gray.light"
      cursor="pointer"
      p="8"
      bg="gray.700"
    >
      <SimpleGrid direction="column" columns={1} spacing={8}>
        Any questions or comments? Leave us a message!
        <Stack direction="row" spacing={3}>
          <Input
            sx={{
              color: "gray",
              _focus: {
                color: "black",
              },
            }}
            variant="filled"
            placeholder="Name"
            name="name"
            value={contact.name}
          />
          <Input
            sx={{
              color: "gray",
              _focus: {
                color: "black",
              },
            }}
            variant="filled"
            placeholder="Last"
            name="last"
            value={contact.last}
          />
        </Stack>
        <Stack spacing={8}>
          <InputGroup>
            <InputLeftElement
              pointerEvents="none"
              children={<EmailIcon color="gray.400" />}
            />
            <Input
              sx={{
                color: "gray",
                _focus: {
                  color: "black",
                },
              }}
              type="email"
              variant="filled"
              placeholder="Email"
              name="email"
              value={contact.email}
            />
          </InputGroup>
          <InputGroup>
            <InputLeftElement
              pointerEvents="none"
              children={<PhoneIcon color="gray.400" />}
            />
            <Input
              sx={{
                color: "gray",
                _focus: {
                  color: "black",
                },
              }}
              variant="filled"
              type="tel"
              placeholder="Phone number"
              name="phone"
              value={contact.phone}
            />
          </InputGroup>
          <RadioGroup
            onChange={handleChange}
            name="reason"
            value={contact.reason}
          >
            <Stack direction="row">
              <Radio value="new_player">New Player</Radio>
              <Radio value="business_inquiry">Business Inquiry</Radio>
              <Radio value="other">Other</Radio>
            </Stack>
          </RadioGroup>
          <Textarea
            sx={{
              color: "gray",
              _focus: {
                color: "black",
              },
            }}
            variant="filled"
            type="text"
            placeholder="Message"
          />
        </Stack>
      </SimpleGrid>
    </Box>
  );
};

export default ContactForm;
