import {
  Accordion,
  AccordionButton,
  AccordionIcon,
  AccordionItem,
  AccordionPanel,
} from "@chakra-ui/accordion";
import { Box, Divider, Flex, Stack } from "@chakra-ui/layout";
import React from "react";

const Schedule = ({ upcoming }) => {
  const formatDateTime = (dateTime) => {
    const date = new Date(dateTime);
    console.log({ date });
    const formattedDate =
      date.toLocaleDateString("en-US", { weekday: "short" }) +
      " " +
      date.toLocaleDateString("en-US", { month: "short" }) +
      " " +
      date.toLocaleDateString("en-US", { day: "2-digit" });
    const time = date.toLocaleString("en-US", {
      hour: "2-digit",
      minute: "2-digit",
    });
    return {
      date: formattedDate,
      time,
    };
  };

  return (
    <Accordion allowMultiple defaultIndex={[0]}>
      {upcoming.map((game) => {
        return (
          <AccordionItem key={game.slug}>
            <h2>
              <AccordionButton>
                <Stack direction="row" textAlign="left" justifyContent="start">
                  <Box>{formatDateTime(game.date).date}</Box>
                  <Divider size="xl" orientation="vertical"></Divider>
                  <Box>
                    {game?.home?.team_name} - {game?.away?.team_name}
                  </Box>
                </Stack>
                <AccordionIcon />
              </AccordionButton>
            </h2>
            <AccordionPanel pb={4}>
              Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do
              eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut
              enim ad minim veniam, quis nostrud exercitation ullamco laboris
              nisi ut aliquip ex ea commodo consequat.
            </AccordionPanel>
          </AccordionItem>
        );
      })}
    </Accordion>
  );
};

export default Schedule;
