import React from "react";
import Link from "next/link";
import Image from "./image";
import { Box, LinkBox, Badge, Text, Flex } from "@chakra-ui/layout";
import styled from "styled-components";
import { background } from "@chakra-ui/styled-system";
import ReactMarkdown from "react-markdown";

const Card = ({
  link,
  as,
  children,
  image,
  border,
  bgColor,
  styles,
  _hover,
  imageProps,
  ...props
}) => {
  const makeClickable = (component) =>
    link ? (
      <Link as={as} href={link}>
        <LinkBox m="16">{component}</LinkBox>
      </Link>
    ) : (
      <>{component}</>
    );

  const hoverStyles = link
    ? {
        transition: "all .2s ease-in-out",
        boxShadow: "0px 5px 10px #212121",
        _hover: {
          transform: "scale(1.02)",
          boxShadow: "0px 5px 10px #21212150",
        },
      }
    : {};

  const overrideStyles = {
    ...styles,
    background: bgColor,
  };

  return makeClickable(
    <Flex
      minW="100%"
      maxW="5xl"
      borderWidth={border ? "2px" : 0}
      overflow="hidden"
      borderRadius="2xl"
      borderColor="gray.dark"
      cursor="pointer"
      sx={{ hoverStyles, ...overrideStyles }}
      bgGradient="gradient.main"
      _hover={_hover}
      flexDirection="column"
      justifyContent="space-around"
      {...props}
    >
      {image && (
        <Box sx={{ display: "flex", flexGrow: 3 }}>
          <Image image={image} {...imageProps} />
        </Box>
      )}
      {children}
    </Flex>
  );
};

export default Card;
