import { Box, Flex, Stack } from "@chakra-ui/layout";
import React from "react";
import Team from "./Team";

const GameInfo = ({ homeTeam, awayTeam, date, location, preview }) => {
  const formatDateTime = (dateTime) => {
    const date = new Date(dateTime);
    const formattedDate =
      date.toLocaleDateString("en-US", { weekday: "short" }) +
      " " +
      date.toLocaleDateString("en-US", { month: "short" }) +
      " " +
      date.toLocaleDateString("en-US", { day: "2-digit" });
    const time = date.toLocaleString("en-US", {
      hour: "2-digit",
      minute: "2-digit",
    });
    return {
      date: formattedDate,
      time,
    };
  };

  return preview ? (
    <Stack direction="column" justifyContent="start" alignItems="start" pt={8}>
      <Team team={homeTeam} preview></Team>
      <Team team={Team} away preview></Team>
    </Stack>
  ) : (
    <Stack direction="row" justifyContent="center" alignItems="start" pt={8}>
      <Team team={homeTeam} preview={preview}></Team>
      {!homeTeam.score && (
        <Stack
          bg={!preview ? "#404040" : "#e2e2e2"}
          borderRadius="8px"
          p="2"
          fontSize="xs"
          alignContent="center"
          justifyContent="center"
          textAlign="center"
        >
          <Box>{formatDateTime(date).time || "FT"}</Box>

          <Flex w="100%" justifyContent="center">
            <Box p="2" fontSize="xs">
              {location}
            </Box>
          </Flex>
        </Stack>
      )}
      <Team team={awayTeam} away preview={preview}></Team>
    </Stack>
  );
};

export default GameInfo;
