import { Flex, Box, Text, SimpleGrid } from "@chakra-ui/layout";
import { Button, Link, LinkBox } from "@chakra-ui/react";
import React, { useState, useEffect } from "react";
import Articles from "../components/articles";
import Card from "../components/ArticleCard";
import Hero from "../components/hero";
import Layout from "../components/layout";
import Seo from "../components/seo";
import { fetchAPI } from "../lib/api";
import GameCard from "../components/GameCard";
import ArticleCard from "../components/ArticleCard";

const Home = ({ articles, categories, homepage, games }) => {
  const [latestArticle, setLatestArticle] = useState(
    articles[articles.length - 1]
  );

  useEffect(() => {
    setLatestArticle(articles[articles.length - 1]);
  }, [articles]);

  const buttons = [
    {
      display: "Shop",
      link: "/shop",
    },
    {
      display: "Contact",
      link: "/contact",
    },
  ];

  return (
    <Layout categories={categories}>
      <Seo seo={homepage.seo} />
      <Hero
        text="St. Louis Bombers Rugby Club"
        image="images/mcb-hero.jpeg"
        buttons={buttons}
      />
      <SimpleGrid columns="2" spacing={8} m="0" p="8" bg="gray.light">
        <SimpleGrid direction="column" spacing={2}>
          <Flex
            justifyContent="space-between"
            direction="row"
            alignContent="center"
          >
            <Text color="gray.100" fontSize="3xl">
              Latest Articles
            </Text>
            <Link>
              <Button as="a" variant="rounded">
                See all
              </Button>
            </Link>
          </Flex>
          <ArticleCard article={latestArticle}></ArticleCard>;
        </SimpleGrid>
        <SimpleGrid columns="1" spacing={4}>
          {games?.map((game) => {
            {
              console.log("ðŸ”¥", { game });
            }
            <>
              <Flex
                justifyContent="space-between"
                direction="row"
                alignContent="center"
              >
                <Text color="gray.100" fontSize="3xl">
                  Next Game
                </Text>
                <Link>
                  <Button as="a" variant="rounded">
                    See all
                  </Button>
                </Link>
              </Flex>
              <GameCard key={game.id} game={game} />;
            </>;
          })}
        </SimpleGrid>
      </SimpleGrid>
    </Layout>
  );
};

export async function getStaticProps() {
  // Run API calls in parallel
  const [articles, categories, homepage, games] = await Promise.all([
    fetchAPI("/articles?status=published"),
    fetchAPI("/categories"),
    fetchAPI("/homepage"),
    fetchAPI("/games"),
  ]);

  return {
    props: { articles, categories, homepage, games },
    revalidate: 1,
  };
}

export default Home;
