import React from "react";
import { Badge, Box, Text, SimpleGrid, Flex, Grid } from "@chakra-ui/layout";
import { Button, Link, LinkBox } from "@chakra-ui/react";

import Card from "../../common/Card";
import Image from "../../common/image";

const GameCard = ({ title, link, linkDisplay, game }) => {
  const gradient = `linear-gradient(
  to bottom right,
  #212121,
  #212121 50%,
  lightgray 50%, 
  lightgray
  )`;

  console.log({ game });

  return (
    <>
      <Flex direction="column" id="card-container">
        <Flex
          id="card-title-bar"
          justifyContent="space-between"
          direction="row"
          alignContent="center"
        >
          <Text color="gray.800" fontSize="3xl">
            {title}
          </Text>
          <Link>
            <Button as="a" variant="rounded" href={link}>
              {linkDisplay}
            </Button>
          </Link>
        </Flex>
        <Flex grow="1">
          {JSON.stringify(game?.home_team, null, 2)}
          <Card bgColor={gradient}>
            <SimpleGrid direction="row" columns="3">
              <Flex alignContent="center" grow="1">
                {/* <Image
                  image={
                    game.image || {
                      alternativeText: "",
                      name: "bomber",
                    }
                  }
                ></Image> */}
                {/* <Text color="gray.800" fontSize="medium">
                  {game.home_team || "ho"}
                </Text> */}
              </Flex>
              <Flex>Time</Flex>
              <Flex>Away</Flex>
            </SimpleGrid>
          </Card>
        </Flex>
      </Flex>
    </>
  );
};

export default GameCard;
