import { Flex, Box, Text, SimpleGrid } from "@chakra-ui/layout";
import { Button, Link, LinkBox } from "@chakra-ui/react";
import React, { useState, useEffect } from "react";
import Articles from "../components/articles";
import Card from "../components/ArticleCard";
import Hero from "../components/hero";
import Layout from "../components/layout";
import Seo from "../components/seo";
import { fetchAPI } from "../lib/api";
import Games from "../components/Games";
import ArticleCard from "../components/ArticleCard";

const Home = (props) => {
  const { articles, categories, homepage, games, ...rest } = props;
  console.log({ props });
  const [latestArticle, setLatestArticle] = useState(
    articles[articles.length - 1]
  );

  useEffect(() => {
    setLatestArticle(articles[articles.length - 1]);
  }, [articles]);

  const buttons = [
    {
      display: "Shop",
      link: "/shop",
    },
    {
      display: "Contact",
      link: "/contact",
    },
  ];

  return (
    <Layout categories={categories} seo={homepage.seo}>
      <Seo seo={homepage.seo} />
      <Hero
        text="St. Louis Bombers Rugby Club"
        image="images/mcb-hero.jpeg"
        buttons={buttons}
      />
      <SimpleGrid
        columns="2"
        spacing={8}
        m="0"
        p="8"
        gridTemplateColumns="repeat(1, 2fr 1fr)"
      >
        <SimpleGrid direction="column" spacing={2} columnSpan={3}>
          <Flex
            justifyContent="space-between"
            direction="row"
            alignContent="center"
          >
            <Text color="gray.800" fontSize="3xl">
              Latest Articles
            </Text>
            <Link>
              <Button as="a" variant="rounded">
                See all
              </Button>
            </Link>
          </Flex>
          <Articles articles={articles} />
        </SimpleGrid>
        <SimpleGrid columnSpan={1}>
          <Games games={games} />
        </SimpleGrid>
      </SimpleGrid>
    </Layout>
  );
};

export async function getStaticProps() {
  // Run API calls in parallel
  const [
    articles,
    categories,
    homepage,
    games,
    players,
    coaches,
  ] = await Promise.all([
    fetchAPI("/articles?status=published"),
    fetchAPI("/categories"),
    fetchAPI("/homepage"),
    fetchAPI("/games"),
    fetchAPI("/players"),
    fetchAPI("/coaches"),
  ]);

  return {
    props: { articles, categories, homepage, games, players, coaches },
    revalidate: 1,
  };
}

export default Home;
