import ReactMarkdown from "react-markdown";
import Moment from "react-moment";
import { fetchAPI } from "../../lib/api";
import Layout from "../../components/layout";
import Image from "../../components/image";
import Seo from "../../components/seo";
import { getStrapiMedia } from "../../lib/media";
import gfm from 'remark-gfm'
import styled  from "styled-components";

const ArticleHeader = styled.div`
font-size: 44px;
color: white;
font-weight: 600;
background: linear-gradient(90deg, rgba(24,24,24,1) 0%, rgba(33,33,33,1) 35%, rgba(48,48,48,1) 100%);
padding: 16px;
border: 4px solid white;
width: 100%;
text-align: center;
`;

const Article = ({ article, categories }) => {
  const imageUrl = getStrapiMedia(article.image);

  const seo = {
    metaTitle: article.title,
    metaDescription: article.description,
    shareImage: article.image,
    article: true,
  };


  return (
    <Layout categories={categories}>
      <Seo seo={seo} />

      <div
        id="banner"
        className="uk-height-medium uk-flex uk-flex-center uk-flex-middle uk-background-cover uk-light uk-padding"
        data-src={imageUrl}
        data-srcset={imageUrl}
        data-uk-img
      >
        <ArticleHeader>{article.title}</ArticleHeader>
      </div>
      <div className="uk-section">
        <div className="uk-container uk-container-small">

          <ReactMarkdown source={article.content} escapeHtml remarkPlugins={[gfm]}/>
          <hr className="uk-divider-small" />

        </div>
      </div>
    </Layout>
  );
};

export async function getStaticPaths() {
  const articles = await fetchAPI("/articles");

  return {
    paths: articles.map((article) => ({
      params: {
        slug: article.slug,
      },
    })),
    fallback: false,
  };
}

export async function getStaticProps({ params }) {
  const articles = await fetchAPI(
    `/articles?slug=${params.slug}&status=published`
  );
  const categories = await fetchAPI("/categories");

  return {
    props: { article: articles[0], categories },
    revalidate: 1,
  };
}

export default Article;
