import React from "react";
import { Badge, Box, Text, SimpleGrid, Flex } from "@chakra-ui/layout";
import { Button, Link, LinkBox } from "@chakra-ui/react";

import Card from "../../common/Card";
import Image from "../../common/image";

const GameCard = ({ title, link, linkDisplay, game }) => {
  const gradient = `linear-gradient(
  to bottom right,
  #212121,
  #212121 50%,
  lightgray 50%, 
  lightgray
  )`;

  return (
    <>
      <Flex direction="column">
        <Flex
          justifyContent="space-between"
          direction="row"
          alignContent="center"
        >
          <Text color="gray.800" fontSize="3xl">
            {title}
          </Text>
          <Link>
            <Button as="a" variant="rounded" href={link}>
              {linkDisplay}
            </Button>
          </Link>
        </Flex>
        <Flex grow="1">
          <Card bgColor={gradient}>{game.data}</Card>
        </Flex>
      </Flex>
    </>
  );
};

export default GameCard;
