import React from "react";
import Link from "next/link";
import Image from "./image";
import { Box, LinkBox, Badge, Text } from "@chakra-ui/layout";
import styled from "styled-components";
import { background } from "@chakra-ui/styled-system";
import ReactMarkdown from "react-markdown";

const Card = ({ link, as, children, image, border, bgColor, styles }) => {
  const makeClickable = (component) =>
    link ? (
      <Link as={as} href={link} m="16">
        <LinkBox>{component}</LinkBox>
      </Link>
    ) : (
      <>{component}</>
    );

  const hoverStyles = link
    ? {
        transition: "all .2s ease-in-out",
        boxShadow: "0px 5px 10px #212121",
        _hover: {
          transform: "scale(1.02)",
          boxShadow: "0px 5px 10px #21212150",
        },
      }
    : {};

  const overrideStyles = {
    ...styles,
    background: bgColor,
  };

  return makeClickable(
    <Box
      minW="xl"
      maxW="5xl"
      borderWidth={border ? "2px" : 0}
      overflow="hidden"
      borderRadius="2xl"
      borderColor="gray.dark"
      cursor="pointer"
      sx={{ hoverStyles, ...overrideStyles }}
      bgGradient="gradient.main"
    >
      {image && <Image image={image} sx={{ objectFit: "cover" }} />}
      {children}
    </Box>
  );
};

export default Card;
