import {
  Box,
  Center,
  Flex,
  LinkBox,
  Link as ChakraLink,
} from "@chakra-ui/react";
import Link from "next/link";
import React from "react";
import styled from "styled-components";

const DiagonalBg = styled(Flex)`
  background-color: #212121;
  background-image: linear-gradient(135deg, #212121 50% #e2e2e2 50%);
`;

const Nav = ({ categories }) => {
  const navs = [
    {
      name: "news",
      id: "news",
      slug: "news",
    },
    {
      name: "schedule",
      id: "schedule",
      slug: "schedule",
    },
    {
      name: "team",
      id: "team",
      slug: "team",
    },
    {
      name: "contact",
      id: "contact",
      slug: "contact",
    },
    {
      name: "photos",
      id: "photos",
      slug: "photos",
    },
    {
      name: "more",
      id: "more",
      slug: "more",
    },
    {
      name: "shop",
      id: "shop",
      slug: "shop",
    },
  ];

  return (
    <nav>
      <DiagonalBg style={{ minHeight: 100 }}>
        <Center w="100px">
          <Link href={`/`}>
            <a>
              <img src="/images/logo.png" alt="Your Name" />
            </a>
          </Link>
        </Center>
        <Center flex="2" size="150px" justifyContent="space-evenly">
          {navs.map((nav) => (
            <ChakraLink
              href={`/${nav.name}`}
              _hover={{
                color: "#e2e2e2",
                textDecoration: "none",
              }}
            >
              <LinkBox
                className="link"
                key={nav.id}
                color="brand.light"
                _hover={{
                  borderBottom: "3px solid white",
                }}
              >
                {nav.name.toUpperCase()}
              </LinkBox>
            </ChakraLink>
          ))}
        </Center>
        <Flex p="4" flex="1" justifyContent="flex-end" alignItems="flex-end">
          powered by Elephantech
        </Flex>
      </DiagonalBg>
    </nav>
  );
};

export default Nav;
