import { SimpleGrid, Grid, GridItem } from "@chakra-ui/layout";
import { Tab, TabList, TabPanel, TabPanels, Tabs } from "@chakra-ui/react";
import { groupBy } from "lodash";
import React from "react";
import Hero from "../common/Hero";
import Layout from "../common/Layout";
import Seo from "../common/Seo";
import Articles from "../components/Articles/articles";
import Games from "../components/Games";
import { fetchAPI } from "../lib/api";

const Home = (props) => {
  const { articles, categories, homepage, games, ...rest } = props;
  const { d1, d3 } = groupBy(games, "division");
  console.log({ d1, d3 });

  const buttons = [
    {
      display: "Shop",
      link: "/shop",
    },
    {
      display: "Contact",
      link: "/contact",
    },
  ];

  return (
    <Layout categories={categories} seo={homepage.seo}>
      <Seo seo={homepage.seo} />
      <Hero
        text="St. Louis Bombers Rugby Club"
        image="images/mcb-hero.jpeg"
        buttons={buttons}
      />
      <SimpleGrid m="0" p="8">
        <Games games={games} />
        <Grid
          p="8"
          h="200px"
          templateRows="repeat(2, 1fr)"
          templateColumns="repeat(5, 1fr)"
          gap={4}
        >
          <GridItem rowSpan={2} colSpan={3} bg="tomato">
            <Articles articles={articles}></Articles>
          </GridItem>
          <GridItem colSpan={2} bg="papayawhip" />
          <GridItem colSpan={2} bg="papayawhip" />
        </Grid>
      </SimpleGrid>
    </Layout>
  );
};

export async function getStaticProps() {
  // Run API calls in parallel
  const [
    articles,
    categories,
    homepage,
    games,
    players,
    coaches,
  ] = await Promise.all([
    fetchAPI("/articles?status=published"),
    fetchAPI("/categories"),
    fetchAPI("/homepage"),
    fetchAPI("/games"),
    fetchAPI("/players"),
    fetchAPI("/coaches"),
  ]);

  return {
    props: { articles, categories, homepage, games, players, coaches },
    revalidate: 1,
  };
}

export default Home;
