import React from "react";
import Link from "next/link";
import Image from "../common/image";
import { Box, LinkBox, Badge, Text, Flex } from "@chakra-ui/layout";
import styled from "styled-components";
import { background } from "@chakra-ui/styled-system";
import ReactMarkdown from "react-markdown";

const ArticleCard = ({ article, styles }) => {
  return (
    <Link
      as={`/article/${article.slug || "hello"}`}
      href="/article/[id]"
      m="16"
    >
      <LinkBox>
        <Box
          maxW="5xl"
          borderWidth="1px"
          overflow="hidden"
          borderRadius="8px"
          borderColor="brand.400"
          cursor="pointer"
          sx={{
            ...styles,
            transition: "all .2s ease-in-out",
            // boxShadow: "0px 5px 10px #212121",
            _hover: {
              transform: "scale(1.05)",
              boxShadow: "0px 5px 10px #21212150",
            },
          }}
          bgGradient="gradient.main"
        >
          <Image image={article.image} sx={{ objectFit: "cover" }} />
          <Box p="4" borderBottom="1px solid black"></Box>
          <Box d="flex" alignItems="baseline" m={4}>
            <Badge
              borderRadius="full"
              px="2"
              bg="brand.800"
              color="brand.400"
              mr="4"
            >
              {article.category.name}
            </Badge>
            <Box
              color="gray.500"
              fontWeight="semibold"
              letterSpacing="wide"
              fontSize="xs"
              textTransform="uppercase"
            >
              {new Date(article.publishedAt).toLocaleDateString()}
            </Box>
          </Box>

          <Flex
            alignItens="start"
            direction="column"
            justifyContent="flex-start"
            m={4}
          >
            <Box fontWeight="semibold" as="h1" lineHeight="tight" isTruncated>
              <Text fontSize="2xl">{article.title}</Text>
            </Box>
            <ReactMarkdown>{article.description}</ReactMarkdown>
          </Flex>
        </Box>
      </LinkBox>
    </Link>
  );
};

export default ArticleCard;
