import { Box, Flex, SimpleGrid } from "@chakra-ui/layout";
import { Text } from "@chakra-ui/react";
import React from "react";
import Card from "../common/Card";

const PlayerList = ({ list = [] }) => {
  console.log({ list });
  const forwards = list?.filter(
    (player) => player.position > 0 && player.position <= 8
  );
  const backs = list?.filter((player) => player.position >= 9);

  const getPosition = (position) => {
    return {
      1: "Loose Head Prop",
      2: "Hooker",
      3: "Tight Head Prop",
      4: "Lock",
      5: "Lock",
      6: "Flanker",
      7: "Flanker",
      8: "8",
      9: "Scrum-half",
      10: "Fly-half",
      11: "Wing",
      12: "Inside Center",
      13: "Outside Center",
      14: "Wing",
      15: "Fullback",
    }[position];
  };
  return (
    <div>
      <header>
        <h3>Forwards</h3>
      </header>
      <SimpleGrid columns={4} spacing="8" m="8">
        {forwards.map((player) => {
          console.log({ pic: player.picture.name });
          const image = {
            name: player.picture.name,
            alternativeText: `${player.first_name}-pro-pic`,
            url: player.picture.url,
          };

          const style = {
            backgroundImage: `url(http://localhost:1339${player.picture.url})`,
          };

          return (
            <Card
              styles={{ minHeight: "300px", height: "auto", minWidth: "auto" }}
              m="8"
              image={image}
              imageProps={{
                h: "100%",
              }}
            >
              <Flex
                bg="brand.800"
                p="4"
                alignContent="end"
                direction="column"
                justifyContent="center"
                borderRadius="10"
              >
                <Text
                  color="brand.400"
                  textAlign="center"
                  fontWeight="bold"
                  textTransform="capitalize"
                >
                  {player.first_name} "{player.nickname}" {player.last_name}
                </Text>
                <Text
                  color="brand.400"
                  textAlign="center"
                  fontWeight="medium"
                  textTransform="capitalize"
                >
                  {getPosition(player.position)}
                </Text>
              </Flex>
            </Card>
          );
        })}
      </SimpleGrid>

      <header>
        <h3>Backs</h3>
      </header>
      {backs.map((player) => {
        return (
          <Card image={player.image}>
            <Box>
              {player.name}
              {player.position}
            </Box>
          </Card>
        );
      })}
    </div>
  );
};

export default PlayerList;
