import React from "react";
import { Badge, Box, Text, SimpleGrid, Flex, Grid } from "@chakra-ui/layout";
import { Button, Link, LinkBox } from "@chakra-ui/react";

import Card from "../../common/Card";
import Image from "../../common/Image";
import GameInfo from "./GameInfo";

const GameCard = ({ title, link, linkDisplay, game = [] }) => {
  const styles = {
    backgroundColor: "#7f8c8d",
    backgroundImage: "linear-gradient(315deg, #7f8c8d 0%, #212121 50%)",
  };

  const { date = "", home, away, location, home_score, away_score } = game;

  const gameInfo = {
    homeTeam: { ...home, score: home_score },
    awayTeam: { ...away, score: away_score },
    location: location,
    date: date,
  };

  return (
    <>
      <Flex direction="column" id="card-container">
        <Flex grow="1">
          <Card styles={styles}>
            <GameInfo {...gameInfo} preview></GameInfo>
          </Card>
        </Flex>
      </Flex>
    </>
  );
};

export default GameCard;
