import React from "react";
import Link from "next/link";
import Image from "../common/image";
import { Box, LinkBox, Badge, Text } from "@chakra-ui/layout";
import styled from "styled-components";
import { background } from "@chakra-ui/styled-system";
import ReactMarkdown from "react-markdown";

const ArticleCard = ({ article }) => {
  return (
    <Link
      as={`/article/${article.slug || "hello"}`}
      href="/article/[id]"
      m="16"
    >
      <LinkBox>
        <Box
          maxW="5xl"
          maxH="3xl"
          borderWidth="2px"
          overflow="hidden"
          borderRadius="8px"
          borderColor="brand.400"
          cursor="pointer"
          sx={{
            transition: "all .2s ease-in-out",
            boxShadow: "0px 5px 10px #212121",
            _hover: {
              transform: "scale(1.05)",
              boxShadow: "0px 5px 10px #21212150",
            },
          }}
          bgGradient="gradient.main"
        >
          <Image image={article.image} sx={{ objectFit: "cover" }} />
          <Box p="4"></Box>
          <Box d="flex" alignItems="baseline" m={8}>
            <Badge borderRadius="full" px="2" bg="brand.800" color="brand.400">
              {article.category.name}
            </Badge>
            <Box
              color="gray.500"
              fontWeight="semibold"
              letterSpacing="wide"
              fontSize="xs"
              textTransform="uppercase"
            >
              {new Date(article.publishedAt).toLocaleDateString()}
            </Box>
          </Box>

          <Box bg="brand.transparent" p={8} m={8}>
            <Box
              mt="2"
              fontWeight="semibold"
              as="h1"
              lineHeight="tight"
              isTruncated
              pb={8}
            >
              <Text fontSize="2xl">{article.title}</Text>
            </Box>
            <ReactMarkdown>{article.description}</ReactMarkdown>
          </Box>
        </Box>
      </LinkBox>
    </Link>
  );
};

export default ArticleCard;
