      <SimpleGrid columns={4}>
        {rightArticles.map((article, i) => {
          return (
            <ArticleCard
              article={article}
              key={`article__left__${article.slug}`}
            />
          );
        })}
      </SimpleGrid>
    </SimpleGrid>
  );
};

export default Articles;

