import { Flex, Box, Text, SimpleGrid } from "@chakra-ui/layout";
import { Button, Link, LinkBox } from "@chakra-ui/react";
import React, { useState, useEffect } from "react";
import Articles from "../components/articles";
import Card from "../components/ArticleCard";
import Hero from "../components/hero";
import Layout from "../components/layout";
import Seo from "../components/seo";
import Games from "../components/Games";
import ArticleCard from "../components/ArticleCard";
import { getStaticProps } from "../lib/props";

const Home = (props) => {
  console.log({ props });
  const { articles, categories, homepage, games, ...rest } = props;
  const [latestArticle, setLatestArticle] = useState(
    articles[articles.length - 1]
  );

  useEffect(() => {
    setLatestArticle(articles[articles.length - 1]);
  }, [articles]);

  const buttons = [
    {
      display: "Shop",
      link: "/shop",
    },
    {
      display: "Contact",
      link: "/contact",
    },
  ];

  return (
    <Layout categories={categories}>
      <Seo seo={homepage.seo} />
      <Hero
        text="St. Louis Bombers Rugby Club"
        image="images/mcb-hero.jpeg"
        buttons={buttons}
      />
      <SimpleGrid columns="2" spacing={8} m="0" p="8">
        <SimpleGrid direction="column" spacing={2}>
          <Flex
            justifyContent="space-between"
            direction="row"
            alignContent="center"
          >
            <Text color="gray.800" fontSize="3xl">
              Latest Articles
            </Text>
            <Link>
              <Button as="a" variant="rounded">
                See all
              </Button>
            </Link>
          </Flex>
          <ArticleCard article={latestArticle}></ArticleCard>;
        </SimpleGrid>
        <SimpleGrid>
          <Games games={games} />
        </SimpleGrid>
      </SimpleGrid>
    </Layout>
  );
};

const { props } = getStaticProps();

export default Home;
