import { Avatar } from "@chakra-ui/avatar";
import { Text, Flex, Box } from "@chakra-ui/layout";
import React from "react";
import Image from "../../common/Image";

const Team = ({ team: { name, score, logo }, away }) => {
  const uploadsUrl = (url) => `http://localhost:1339${url}`;

  return (
    <>
      <Flex
        justifyContent="space-between"
        alignItems="center"
        direction={away ? "row-reverse" : "row"}
      >
        <Box>
          <Text
            fontWeight={name === "Bombers" ? "extrabold" : "normal"}
            my={0}
            mx={2}
            fontSize="md"
            fontFamily="body"
            textTransform="capitalize"
          >
            {name}
          </Text>
        </Box>
        <Box>
          <Avatar
            bg="brand.200"
            showBorder
            size="sm"
            name={name}
            src={uploadsUrl(logo)}
          />
        </Box>
        <Box>
          <Text
            fontWeight={"extrabold"}
            my={0}
            mx={2}
            fontSize="2xl"
            fontFamily="body"
            textTransform="capitalize"
          >
            {score}
          </Text>
        </Box>
      </Flex>
    </>
  );
};

export default Team;
